command.post_template:
{* loop over the ip_access_list instances and build the prefix_set array
based on each  ip_access_list src IP and mask variables *}
{$idx=0}
{if isset($ip_access_list)}
	{foreach $ip_access_list as $ip_acl}
		{if isset($ip_acl.access_list) && ($ip_acl.object_id == $params.object_id)}
			{foreach $ip_acl.access_list as $acl}
				{if isset($acl.acl_src_ip) && isset($acl.acl_src_mask_length)}
					{assign_object_variable 
						var="prefix_set.{$idx}.src_ip" 
						value="{$acl.acl_src_ip}"}
					{assign_object_variable 
						var="prefix_set.{$idx}.mask_length" 
						value="{$acl.acl_src_mask_length}"}
					{$idx=$idx+1}
				{else if isset($acl.acl_src) && ($acl.acl_src == "any")}
					{assign_object_variable 
						var="prefix_set.{$idx}.src_ip" 
						value="0.0.0.0"}
					{assign_object_variable 
						var="prefix_set.{$idx}.mask_length" 
						value="0"}				
					{$idx=$idx+1}
				{else if isset($acl.acl_src_ip) && !isset($acl.acl_src_mask_length)}
					{assign_object_variable 
						var="prefix_set.{$idx}.src_ip" 
						value="{$acl.acl_src_ip}"}
					{assign_object_variable 
						var="prefix_set.{$idx}.mask_length" 
						value="32"}				
					{$idx=$idx+1}
				{/if}
			{/foreach}
			{* update the object_id by adding "prefix_set_" prefix *}
			{break}
		{/if}
	{/foreach}
{/if}
{$idx=0}
{if isset($ip_prefix_list)}
	{foreach $ip_prefix_list as $ip_pref_list}
		{if isset($ip_pref_list.name) && ($ip_pref_list.name == $params.object_id)}
			{if isset($ip_pref_list.description)}
				{assign_object_variable 
					var="description" 
					value="{$ip_pref_list.description}"}

			{/if}
			{if isset($ip_pref_list.prefix_ip) && isset($ip_pref_list.prefix_mask_length)}
				{assign_object_variable 
					var="prefix_set.{$idx}.src_ip" 
					value="{$ip_pref_list.prefix_ip}"}
				{assign_object_variable 
					var="prefix_set.{$idx}.mask_length" 
					value="{$ip_pref_list.prefix_mask_length}"}
				{if isset($ip_pref_list.comp_prefix_length) && isset($ip_pref_list.prefix_length)}
					{assign_object_variable 
						var="prefix_set.{$idx}.comp_prefix_length" 
						value="{$ip_pref_list.comp_prefix_length}"}
					{assign_object_variable 
						var="prefix_set.{$idx}.prefix_length" 
						value="{$ip_pref_list.prefix_length}"}
				{/if}
				{$idx=$idx+1}
			{/if}
		{/if}
	{/foreach}
{/if}
{assign_object_variable 
	var="object_id" 
	value="pfx_{$params.object_id}"}
