command.post_template:
{$route_idx=0}
{$_vrf_routes=[]}

{* loop through the ip route and process the VRF routes to build a local array $_vrf_routes with the VRF routes *}
{foreach $ip_route as $route}
	{$vrf_idx=0}
	{if $route.vrf_name && isset($route.vrf_name)}
		{$mask=$route.ipv4_mask}
		{capture name="mask_length"}
		{get_mask_len mask=$mask var="mask_length"}
		{/capture}
		{* check if the VRF was already found *}
		{$found=false}
		{foreach $_vrf_routes as $_vrf_route}
			{if $_vrf_route.vrf_name == $route.vrf_name}
				{$found=true}
				{break}
			{/if}
			{$vrf_idx=$vrf_idx+1}
		{/foreach}
		{if !$found}
			{* if the VRF is a newly found one, initialize the VRF $routes array by adding data as the first element (index 0) *}
			{$_vrf_routes.{$vrf_idx}.vrf_name=$route.vrf_name}
			{$_vrf_routes.{$vrf_idx}.routes=[]}
			{$_vrf_routes.{$vrf_idx}.routes.0.interface=$route.interface}
			{$_vrf_routes.{$vrf_idx}.routes.0.ipv4_address=$route.ipv4_address}
			{$_vrf_routes.{$vrf_idx}.routes.0.ipv4_mask_length=$mask_length}
			{if isset($route.gateway)}
				{$_vrf_routes.{$vrf_idx}.routes.0.gateway=$route.gateway}
			{/if}
			{if isset($route.metric)}
				{$_vrf_routes.{$vrf_idx}.routes.0.metric=$route.metric}
			{/if}
			{if isset($route.tag)}
				{$_vrf_routes.{$vrf_idx}.routes.0.tag=$route.tag}
			{/if}
		{else}
			{* if it's a VRF that was already processed, add the new route to the $routes array *}
			{foreach $_vrf_routes.{$vrf_idx}.routes as $_vrf_route}
			{* simple foreach to get the size of $_vrf_route array *}
				{break}
			{/foreach}
			{$route_idx=$_vrf_route@total}
			{if isset($route.interface)}
				{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.interface=$route.interface}
			{/if}
			{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.ipv4_address=$route.ipv4_address}
			{if isset($route.gateway)}
				{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.gateway=$route.gateway}
			{/if}
			{if isset($route.metric)}
				{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.metric=$route.metric}
			{/if}
			{if isset($route.tag)}
				{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.tag=$route.tag}
			{/if}
			{$_vrf_routes.{$vrf_idx}.routes.{$route_idx}.ipv4_mask_length=$mask_length}
		{/if}
	{/if}
{/foreach}

{* iterate through the local array $_vrf_routes and assign the values to the microservice variable $vrf_routes *}
{foreach $_vrf_routes as $_vrf_route}
	{assign_object_variable
        var="vrf_routes.{$_vrf_route@index}.vrf_name"
        value="{$_vrf_route.vrf_name}"}
	{foreach $_vrf_route.routes as $_route}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.ipv4_address" value="{$_route.ipv4_address}"}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.ipv4_mask_length" value="{$_route.ipv4_mask_length}"}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.interface" value="{$_route.interface}"}
		{if isset($_route.gateway)}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.gateway" value="{$_route.gateway}"}
		{if isset($_route.metric)}
		{/if}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.metric" value="{$_route.metric}"}
		{/if}
		{if isset($_route.tag)}
		{assign_object_variable var="vrf_routes.{$_vrf_route@index}.routes.{$_route@index}.tag" value="{$_route.tag}"}
		{/if}
	{/foreach}
{/foreach}

{* process the routes that are not VRF routes *}
{foreach $ip_route as $route}
	{if !$route.vrf_name}

	    {capture}
	    {* transform the mask to the mask length for compatibility with XR *}
	    {get_mask_len mask=$mask var="mask_length"}
	    {/capture}
	    {$mask=$route.ipv4_mask}
		{assign_object_variable
			var="routes.{$route_idx}.ipv4_address" 
			value="{$route.ipv4_address}"}
		{assign_object_variable 
			var="routes.{$route_idx}.ipv4_mask_length" 
			value="{$mask_length}"}
		{if isset($route.interface)}
			{assign_object_variable 
				var="routes.{$route_idx}.interface" 
				value="{$route.interface}"}
		{/if}
		{if isset($route.gateway)}
			{assign_object_variable 
				var="routes.{$route_idx}.gateway" 
				value="{$route.gateway}"}
		{/if}
		{if isset($route.metric)}
			{assign_object_variable 
				var="routes.{$route_idx}.metric" 
				value="{$route.metric}"}
		{/if}
		{if	isset($route.tag)}
			{assign_object_variable 
				var="routes.{$route_idx}.tag" 
				value="{$route.tag}"}
		{/if}
		{$route_idx=$route_idx+1}
	{/if}
{/foreach}
