command.post_template:
{assign var="i" value=0}
{foreach $params.times_monday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="monday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_tuesday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="tuesday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_wednesday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="wednesday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_thursday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="thursday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_friday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="friday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_saturday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="saturday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_sunday as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.day_of_the_week" value="sunday"}
{assign var="i" value=$i+1}
{/foreach}
{foreach $params.times_nr as $t}
{assign_object_variable var="times.$i.start_time" value=$t.start_time}
{assign_object_variable var="times.$i.start_date" value=$t.start_date}
{assign_object_variable var="times.$i.end_time" value=$t.end_time}
{assign_object_variable var="times.$i.end_date" value=$t.end_date}
{assign var="i" value=$i+1}
{/foreach}
